def santa_position(movements):    
    position = 0
    moves = 0
    has_entered_basement = False

    for movement in movements:
        moves += 1
        
        if movement == '(':
            position += 1
        else:
            position -= 1
            
            if position == -1 and not has_entered_basement:
                print('Entering basement after move:', moves)
                has_entered_basement = True
                
    print('Final floor:', position)

santa_position(moves)