class Test_Day_1_Part_1(unittest.TestCase):

    def test_no_moves(self):
        santa = sut.Santa()
        floor = santa.get_floor('')
        self.assertEqual(floor, 0)

    def test_one_move(self):
        santa = sut.Santa()
        floor = santa.get_floor('(')
        self.assertEqual(floor, 1)

    def test_one_down(self):
        santa = sut.Santa()
        floor = santa.get_floor(')')
        self.assertEqual(floor, -1)

    def test_triple(self):
        santa = sut.Santa()
        floor = santa.get_floor('(((')
        self.assertEqual(floor, 3)

    def test_sample_data(self):
        santa = sut.Santa()
        floor = santa.get_floor('))(((((')
        self.assertEqual(floor, 3)

    def test_moves_code(self):
        santa = sut.Santa()
        floor = santa.get_floor(moves_code)
        self.assertEqual(floor, 232)       

class Test_Day_1_Part_2(unittest.TestCase):

    def test_first_move_enters_basement(self):
        santa = sut.Santa()
        position = santa.check_basement(')')
        self.assertEqual(position, 1) 

    def test_third_move_enters_basement(self):
        santa = sut.Santa()
        position = santa.check_basement('())')
        self.assertEqual(position, 3) 
    
    def test_third__out_of_four_move_enters_basement(self):
        santa = sut.Santa()
        position = santa.check_basement('())(')
        self.assertEqual(position, 3) 

    def test_moves_code(self):
        santa = sut.Santa()
        position = santa.check_basement(moves_code)
        self.assertEqual(position, 1783) 

if __name__ == '__main__':
    unittest.main()

